import React, { Component } from 'react';
import Header from './components/header/Header';
import Player from './page/player/player';
import {MUSIC_LIST} from './components/musiclist/mysiclist';

const $ = window.$;

class App extends Component { 
  constructor (props) {
    super(props);

    this.state = {
      currentMusicItem: MUSIC_LIST[0]
    }
  }
  componentDidMount () {
    $('#player').jPlayer({
      ready: function () {
        $(this).jPlayer('setMedia', {
            mp3:  'http://oj4t8z2d5.bkt.clouddn.com/%E9%A3%8E%E7%BB%A7%E7%BB%AD%E5%90%B9.mp3'
        }).jPlayer('play');
      },
      supplied: 'mp3',
      wmode: 'window'
    });
  }

  render() {
    return (
      <div className="App">
        <Header />
        <Player currentMusicItem={this.currentMusicItem}/>
      </div>
    );
  }
}

export default App;
