import React, { Component } from 'react';
import Header from './components/header/Header';
import Player from './page/player/Player';
import { MUSIC_LIST } from './components/musiclist/MusicList';
import MusicList from './page/musiclist/MusicList';
import { BrowserRouter, Route, NavLink} from 'react-router-dom';

const $ = window.$;

class App extends Component { 
  constructor (props) { 
    super(props);

    this.state = {
      musicList: MUSIC_LIST,
      currentMusicItem: MUSIC_LIST[1]
    }
  }
  componentDidMount () {
    $('#player').jPlayer({
      ready: function () {
        $(this).jPlayer('setMedia', {
            mp3:  'http://oj4t8z2d5.bkt.clouddn.com/%E9%A3%8E%E7%BB%A7%E7%BB%AD%E5%90%B9.mp3'
        }).jPlayer('play');
      },
      supplied: 'mp3',
      wmode: 'window'
    });
  }
  // React.cloneElement(this.props.children, this.state)
  // {this.props.children}
  // <MusicList 
  //   currentMusicItem={this.state.currentMusicItem}
  //   musicList={this.state.musicList}
  // />
  render() {
    return (
      <div className="App">
        <Header />
        <BrowserRouter>
        <div className="App">
            <Header />
            <Route exact path="/" component={Player}></Route>
            <Route path="/list" component={MusicList}></Route>
        </div>
      </BrowserRouter>
      </div>
    );
  }
}

